import { ReactElement, createElement } from "react";
import { ValueStatus } from 'mendix';
import { FileDropperUI } from "./components/FileDropperUI";
import { FiledropperContainerProps } from "../typings/FiledropperProps";

import "./ui/Filedropper.css";
const defaultUploadImage = "data:image/png;base64,";

export function Filedropper({
    fileDataAttr,
    onDropAction,
    defaultText,
    dragText,
    buttonText,
    uploadImageType,
    uploadImage,
    uploadIcon,
    maxFileSize,
    maxNumFilesToUpload,
    acceptedFileTypes,
    acceptedFilesText,
    acceptedFileSizeText,
    class: className
}: FiledropperContainerProps): ReactElement {

    if (fileDataAttr === null || fileDataAttr === undefined || fileDataAttr.status != ValueStatus.Available || defaultText?.status != ValueStatus.Available || dragText?.status != ValueStatus.Available || buttonText?.status != ValueStatus.Available || acceptedFilesText?.status != ValueStatus.Available || acceptedFileSizeText?.status != ValueStatus.Available ) {
        return <div />;
    }

    const getAcceptedFileTypes = () => {
        return Array.from(acceptedFileTypes.split(","), type => type.toLowerCase().trim());
    }

    const renderUploadImage = () => {
        if (uploadIcon === undefined && uploadImage === undefined) {
            return <img className="uploadImage" src={defaultUploadImage} />
        }
        else {
            if (uploadImageType === "image") {
                return <img className="uploadImage" src={uploadImage?.value?.uri} />
            }
            else {
                //@ts-ignore
                return <span className={"uploadIcon glyphicon " + uploadIcon?.value?.iconClass} />
            }
        }
    }

    return (
        <div className={"dropzoneWrapper " + className}>
            <FileDropperUI
                fileDataAttr={fileDataAttr}
                onDropAction={onDropAction}
                defaultText={defaultText ? defaultText.value : "Drag 'n' drop some files here, or click to select files"}
                dragText={dragText ? dragText.value : "Drop the files here ..."}
                buttonText={buttonText ? buttonText.value : "Select files"}
                uploadImage={renderUploadImage()}
                maxFileSize={maxFileSize}
                maxNumFilesToUpload={maxNumFilesToUpload}
                acceptedFileTypes={getAcceptedFileTypes()}
                acceptedFilesText={acceptedFilesText ? acceptedFilesText?.value : `File types accepted: ${acceptedFileTypes}`}
                acceptedFileSizeText={acceptedFileSizeText ? acceptedFileSizeText.value : `Max file size: ${maxFileSize} Mb`}
            />
        </div>
    )
}
